$(document).ready(function () {
    $('#change-certificate').click(function () {
        checkPlugin(checkPluginCallback);
    });
});

/**
 * 
 * @param {any} functionCallback
 */
function checkPlugin(functionCallback) {
    //showLoading('Đang kiểm tra cài đặt Vnpt-CA Plugin...');
    vnpt_plugin.checkPlugin(functionCallback);
}

/**
 * 
 * @param {any} data
 */
function checkPluginCallback(data) {
    if (data === "1") {
        setLicense(setLicenseCallback);
        vnpt_plugin.SetGetCertFromUsbToken(false);
    }
    else {
        alert("VNPT-CA Plugin chưa được cài đặt hoặc chưa được bật");
    }
}

/**
 * 
 * @param {any} functionCallback
 */
function setLicense(functionCallback) {
    //changeLoadingText('Đang kiểm tra License...');
    var key = "";
    vnpt_plugin.setLicenseKey(key, functionCallback);
}

/**
 * 
 */
function setLicenseCallback() {
    vnpt_plugin.getCertInfo(showCertificateList);
}

/**
 * 
 * @param {any} data
 */
function showCertificateList(data) {
    if (data === "" || data === null) {
        alert("Không lấy được thông tin chứng thư số từ usb token");
        return;
    }
    var jsOb = JSON.parse(data);

    console.log(jsOb);

    if (jsOb !== "" || jsOb === null) {
        if (jsOb.hasOwnProperty('code')) {
            alert(jsOb.error);
            return;
        }
        $('#cert-subject').val(jsOb.subjectCN);
        $('#cert-issuer').val(jsOb.issuerCN);
        $('#cert-serial').val(jsOb.serial);
        $('#cert-valid-from').val(jsOb.notBefore);
        $('#cert-valid-to').val(jsOb.notAfter);
        $('#cert-base64').val(jsOb.base64);
        $('#cert-modal').modal('show');
    } else {
        alert("Không thể kiểm tra thông tin chứng thư số");
    }
}